import Head from 'next/head';
import Link from 'next/link';
import Router from 'next/router';

Router.events.on('routerChangeStart',(url)=>{
    if(url==='/films'||url==='/f'){
        location.href='./nopermission'
    }
})

export default ({ children }) => (
    <div>
        <Head>
            <title>共通头部测试</title>
        </Head>
        <div>
            <Link href="/" prefetch>主页</Link>|
            {/* <Link as='路由遮盖' href="/list">列表</Link>| */}
            <Link href="/list">列表</Link>|
            <Link as='f' href="/films">电影</Link>
        </div>
        {children}
        <footer>工程中心</footer>
    </div>
)
