import React, { Component } from 'react';
// import axios from 'axios';

export default class Files extends Component {
    static async getInitialProps() {
        // const res = await axios.get('https://m.maizuo.com/gateway?cityId=110100&pageNum=1&pageSize=10&type=1&k=9536231', {
        //     headers: {
        //         'X-Host': 'mall.film-ticket.film.list'
        //     }
        // });
     fetch('https://m.maizuo.com/gateway?cityId=110100&pageNum=1&pageSize=10&type=1&k=9536231', {
            method: 'GET',
            mode: 'cors',
            headers: {
                'X-Host': 'mall.film-ticket.film.list'
            },
        }).then(res => {
            return res.json();
        }).then(json => {
            console.log('res', res)
        })
        return {
            // films: res.data.data.films
            films: []
        }
    }
    render() {
        <div>
            <h2>电影</h2>
            <ul>
                {
                    this.props.films.map(item => {
                        return <li key={item.filmId}>
                            <img src={item.poster} />
                            {item.name}
                        </li>
                    })
                }
            </ul>
        </div>
    }
}
